ode((c & 63) | 128);\\n            } else {\\n                utftext += String.fromCharCode((c >> 12) | 224);\\n                utftext += String.fromCharCode(((c >> 6) & 63) | 128);\\n                utftext += String.fromCharCode((c & 63) | 128);\\n            }\\n        }\\n        return utftext;\\n    };\\n    var x = Array();\\n    var k, AA, BB, CC, DD, a, b, c, d;\\n    var S11 = 7, S12 = 12, S13 = 17, S14 = 22;\\n    var S21 = 5, S22 = 9, S23 = 14, S24 = 20;\\n    var S31 = 4, S32 = 11, S33 = 16, S34 = 23;\\n    var S41 = 6, S42 = 10, S43 = 15, S44 = 21;\\n    string = md5_Utf8Encode(string);\\n    x = md5_ConvertToWordArray(string);\\n    a = 0x67452301;\\n    b = 0xEFCDAB89;\\n    c = 0x98BADCFE;\\n    d = 0x10325476;\\n    for (k = 0; k < x.length; k += 16) {\\n        AA = a;\\n        BB = b;\\n        CC = c;\\n        DD = d;\\n        a = md5_FF(a, b, c, d, x[k + 0], S11, 0xD76AA478);\\n        d = md5_FF(d, a, b, c, x[k + 1], S12, 0xE8C7B756);\\n        c = md5_FF(c, d, a, b, x[k + 2], S13, 0x242070DB);\\n        b = md5_FF(b, c, d, a, x[k + 3], S14, 0xC1BDCEEE);\\n        a = md5_FF(a, b, c, d, x[k + 4], S11, 0xF57C0FAF);\\n        d = md5_FF(d, a, b, c, x[k + 5], S12, 0x4787C62A);\\n        c = md5_FF(c, d, a, b, x[k + 6], S13, 0xA8304613);\\n        b = md5_FF(b, c, d, a, x[k + 7], S14, 0xFD469501);\\n        a = md5_FF(a, b, c, d, x[k + 8], S11, 0x698098D8);\\n        d = md5_FF(d, a, b, c, x[k + 9], S12, 0x8B44F7AF);\\n        c = md5_FF(c, d, a, b, x[k + 10], S13, 0xFFFF5BB1);\\n        b = md5_FF(b, c, d, a, x[k + 11], S14, 0x895CD7BE);\\n        a = md5_FF(a, b, c, d, x[k + 12], S11, 0x6B901122);\\n        d = md5_FF(d, a, b, c, x[k + 13], S12, 0xFD987193);\\n        c = md5_FF(c, d, a, b, x[k + 14], S13, 0xA679438E);\\n        b = md5_FF(b, c, d, a, x[k + 15], S14, 0x49B40821);\\n        a = md5_GG(a, b, c, d, x[k + 1], S21, 0xF61E2562);\\n        d = md5_GG(d, a, b, c, x[k + 6], S22, 0xC040B340);\\n        c = md5_GG(c, d, a, b, x[k + 11], S23, 0x265E5A51);\\n        b = md5_GG(b, c, d, a, x[k + 0], S24, 0xE9B6C7AA);\\n        a = md5_GG(a, b, c, d, x[k + 5], S21, 0xD62F105D);\\n        d = md5_GG(d, a, b, c, x[k + 10], S22, 0x2441453);\\n        c = md5_GG(c, d, a, b, x[k + 15], S23, 0xD8A1E681);\\n        b = md5_GG(b, c, d, a, x[k + 4], S24, 0xE7D3FBC8);\\n        a = md5_GG(a, b, c, d, x[k + 9], S21, 0x21E1CDE6);\\n        d = md5_GG(d, a, b, c, x[k + 14], S22, 0xC33707D6);\\n        c = md5_GG(c, d, a, b, x[k + 3], S23, 0xF4D50D87);\\n        b = md5_GG(b, c, d, a, x[k + 8], S24, 0x455A14ED);\\n        a = md5_GG(a, b, c, d, x[k + 13], S21, 0xA9E3E905);\\n        d = md5_GG(d, a, b, c, x[k + 2], S22, 0xFCEFA3F8);\\n        c = md5_GG(c, d, a, b, x[k + 7], S23, 0x676F02D9);\\n        b = md5_GG(b, c, d, a, x[k + 12], S24, 0x8D2A4C8A);\\n        a = md5_HH(a, b, c, d, x[k + 5], S31, 0xFFFA3942);\\n        d = md5_HH(d, a, b, c, x[k + 8], S32, 0x8771F681);\\n        c = md5_HH(c, d, a, b, x[k + 11], S33, 0x6D9D6122);\\n        b = md5_HH(b, c, d, a, x[k + 14], S34, 0xFDE5380C);\\n        a = md5_HH(a, b, c, d, x[k + 1], S31, 0xA4BEEA44);\\n        d = md5_HH(d, a, b, c, x[k + 4], S32, 0x4BDECFA9);\\n        c = md5_HH(c, d, a, b, x[k + 7], S33, 0xF6BB4B60);\\n        b = md5_HH(b, c, d, a, x[k + 10], S34, 0xBEBFBC70);\\n        a = md5_HH(a, b, c, d, x[k + 13], S31, 0x289B7EC6);\\n        d = md5_HH(d, a, b, c, x[k + 0], S32, 0xEAA127FA);\\n        c = md5_HH(c, d, a, b, x[k + 3], S33, 0xD4EF3085);\\n        b = md5_HH(b, c, d, a, x[k + 6], S34, 0x4881D05);\\n        a = md5_HH(a, b, c, d, x[k + 9], S31, 0xD9D4D039);\\n        d = md5_HH(d, a, b, c, x[k + 12], S32, 0xE6DB99E5);\\n        c = md5_HH(c, d, a, b, x[k + 15], S33, 0x1FA27CF8);\\n        b = md5_HH(b, c, d, a, x[k + 2], S34, 0xC4AC5665);\\n        a = md5_II(a, b, c, d, x[k + 0], S41, 0xF4292244);\\n        d = md5_II(d, a, b, c, x[k + 7], S42, 0x432AFF97);\\n        c = md5_II(c, d, a, b, x[k + 14], S43, 0xAB9423A7);\\n        b = md5_II(b, c, d, a, x[k + 5], S44, 0xFC93A039);\\n        a = md5_II(a, b, c, d, x[k + 12], S41, 0x655B59C3);\\n        d = md5_II(d, a, b, c, x[k + 3], S42, 0x8F0CCC92);\\n        c = md5_II(c, d, a, b, x[k + 10], S43, 0xFFEFF47D);\\n        b = md5_II(b, c, d, a, x[k + 1], S44, 0x85845DD1);\\n        a = md5_II(a, b, c, d, x[k + 8], S41, 0x6FA87E4F);\\n        d = md5_II(d, a, b, c, x[k + 15], S42, 0xFE2CE6E0);\\n        c = md5_II(c, d, a, b, x[k + 6], S43, 0xA3014314);\\n        b = md5_II(b, c, d, a, x[k + 13], S44, 0x4E0811A1);\\n        a = md5_II(a, b, c, d, x[k + 4], S41, 0xF7537E82);\\n        d = md5_II(d, a, b, c, x[k + 11], S42, 0xBD3AF235);\\n        c = md5_II(c, d, a, b, x[k + 2], S43, 0x2AD7D2BB);\\n        b = md5_II(b, c, d, a, x[k + 9], S44, 0xEB86D391);\\n        a = md5_AddUnsigned(a, AA);\\n        b = md5_AddUnsigned(b, BB);\\n        c = md5_AddUnsigned(c, CC);\\n        d = md5_AddUnsigned(d, DD);\\n    }\\n    return (md5_WordToHex(a) + md5_WordToHex(b) + md5_WordToHex(c) + md5_WordToHex(d)).toLowerCase();\\n}\\n```\\n#### 4.2、加签代码\\n\\n\\n```javascript\\n\\n/**\\n * 加签\\n * <P>\\n *   验签\\n *  <P>\\n * account=1720696548&password=123456&timestamp=1578811547552\\n * sign,timestamp\\n * String mysign = DigestUtils.md5Hex(getContentBytes(preSignStr + APP_KEY, INPUT_CHARSET));\\n * @author wangsong\\n * @mail  1720696548@qq.com\\n * @date  2021/3/29 0029 22:10\\n * @version 1.0.0\\n */\\nSign = {\\n    /**\\n     * 加签秘钥\\n     */\\n    param: {\\n        appKey: \\\"xijia\\\",\\n        secretKey: \\\"xijia@qwer\\\",\\n    },\\n    /**\\n     * 1、query 参数加签\\n     * <p>\\n     *     1、获取请求参数, 直接获取url ? 后面的参数\\n     *\\n     *     // --- addSing 方法\\n     *     2、追加 timestamp\\n     *     3、参数对象根据 key 排序(包括 timestamp)\\n     *     4、md5(appKey + 排序后的参数 + secretKey)\\n     *\\n     *     // --- 请求\\n     *     5、sign + timestamp 放入 headers 进行请求\\n     *     其他：空val的对象不加入验签范围\\n     * </p>\\n     * @param url 请求的 url\\n     * @param timestamp 时间戳\\n     * @returns {string|null}\\n     */\\n    query: function (url, timestamp) {\\n        if (url.indexOf(\\\"?\\\") === -1) {\\n            return null;\\n        }\\n        // 通过split()分割为数组\\n        let arr = url.split(\'?\')[1].split(\'&\');\\n        // 加签参数, query的所有参数\\n        let theRequest = {};\\n        for (let i = 0; i < arr.length; i++) {\\n            let kye = arr[i].split(\\\"=\\\")[0];\\n            let value = arr[i].split(\\\"=\\\")[1];\\n            if (value != null && value !== \\\"\\\") {\\n                // value不为null给对象赋值，decodeURIComponent目的是为了参数出现 # 等的字符,在请求前进行了encodeURIComponent编码\\n                theRequest[kye] = decodeURIComponent(value);\\n            }\\n        }\\n        return Sign.addSing(theRequest, timestamp);\\n\\n    },\\n\\n    /**\\n     * 2、body 参数加签\\n     * <p>\\n     *     1、获取请求参数, 对 body 的参数内的参数进行key排序 (包括所有下级数据排序), 然后转化为json字符串\\n     *       - 并以 query格式拼接参数，示例: body= JSON(key排序后data)\\n     *\\n     *     // --- addSing 方法\\n     *     2、追加query格式的参数 timestamp\\n     *     3、参数对象根据 key 排序, 只有 body + timestamp 两个参数\\n     *     4、md5(appKey + 排序后的参数 + secretKey)\\n     *\\n     *     // ---- 请求\\n     *     5、sign + timestamp 放入 headers 进行请求\\n     *     其他：空val的对象不加入验签范围\\n     * </p>\\n     * @param data body数据\\n     * @param timestamp 当前数据戳\\n     * @returns {null}\\n     */\\n    body: function (data, timestamp) {\\n        if (data == null) {\\n            return null;\\n        }\\n        // body参数排序\\n        data = Sign.bodyDataSort(data);\\n        // 转为json\\n        let bodyJson = JSON.stringify(data);\\n        // 加签参数\\n        let theRequest = {};\\n        theRequest[\\\"body\\\"] = bodyJson;\\n        return Sign.addSing(theRequest, timestamp);\\n    },\\n\\n    /**\\n     * 3、加签实现\\n     *    3、参数对象根据 key 排序, 只有 body + timestamp 两个参数\\n     *    4、md5(appKey + 排序后的参数 + secretKey)\\n     * @returns {*}\\n     */\\n    addSing: function (theRequest, timestamp) {\\n        theRequest[\\\"timestamp\\\"] = timestamp;\\n        // 排序并重新封装获得排序后的参数\\n        let dataParams = \\\"\\\";\\n        let result = Object.keys(theRequest).sort();\\n        for (let key of result) {\\n            dataParams += \\\"&\\\" + key + \\\"=\\\" + theRequest[key];\\n        }\\n        dataParams = dataParams.substring(1);\\n        // 加签\\n        return md5(Sign.param.appKey + dataParams + Sign.param.secretKey);\\n    },\\n\\n    /**\\n     * 4、body 参数排序\\n     * @returns {*}\\n     */\\n    bodyDataSort: function (data) {\\n        // 数组长度小于2 或 没有指定排序字段 或 不是json格式数据\\n        // 判断是数组还是对象\\n        if (data instanceof Array) {\\n            //  数组\\n            if (data[0] instanceof Object || data[0] instanceof Array) {\\n                let arrays = data;\\n                let newArrays = [];\\n                for (let i = 0; i < arrays.length; i++) {\\n                    let dataTwo = arrays[i];\\n                    // 根据 key 类型排序\\n                    let keysTwo = Object.keys(dataTwo).sort();\\n                    let newDataTwo = {};\\n                    for (let keyTwo of keysTwo) { //遍历json对象的每个key/value对,p为key\\n                        // 递归给下级排序\\n                        if (dataTwo[keyTwo] instanceof Object) {\\n                            newDataTwo[keyTwo] = Sign.bodyDataSort(dataTwo[keyTwo]);\\n                        } else {\\n                            newDataTwo[keyTwo] = dataTwo[keyTwo];\\n                        }\\n                    }\\n                    newArrays.push(newDataTwo);\\n                }\\n                return newArrays;\\n            } else {\\n                // 不处理: [0,1,2,3] 数组数据\\n                return data;\\n            }\\n        } else if (data instanceof Object) {\\n            // 对象\\n            // 根据 key 类型排序\\n            let keys = Object.keys(data).sort();\\n            let newData = {};\\n            for (let key of keys) { //遍历json对象的每个key/value对,p为key\\n                // 递归给下级排序\\n                if (data[key] instanceof Object) {\\n                    newData[key] = Sign.bodyDataSort(data[key]);\\n                } else {\\n                    newData[key] = data[key];\\n                }\\n            }\\n            return newData;\\n        } else {\\n            return data;\\n        }\\n    }\\n};\\n\\n```\\n#### 4.3、加签代码请求处调用\\n```javascript\\n    // 加签\\n    let timestamp = new Date().getTime();\\n    let sign = Sign.query(url, timestamp);\\n    sign = sign != null ? sign : Sign.body(data, timestamp);\\n\\n    // 放入请求头\\n      headers: {\\n          \\\"TOKEN\\\": getGlobalHeaders(),\\n          \\\"timestamp\\\": timestamp,\\n          \\\"sign\\\": sign,\\n       },\\n```\\n\\n\\n\",\"helpVersion\":0,\"id\":\"1377844447580094465\",\"sort\":0,\"title\":\"接口验签\"}]', '{\"code\":200,\"data\":\"1377844447580094465\",\"msg\":\"编辑成功\"}', 1, 244, 242);
INSERT INTO `t_xj_admin_log` VALUES ('1377868416811044865', NULL, NULL, '2021-04-02 14:20:04', '2021-04-02 14:20:04', 0, 0, '╥﹏╥', '0', 0, 'http://xijia.plus/', 'http://xijia.plus/api/admin/adminUser/login', '/api/admin/adminUser/login', '125.70.195.54', '127.0.0.1', 'POST', 'xijia.plus', '51420', 'com.ws.ldy.modules.sys.admin.controller', 'com.ws.ldy.modules.sys.admin.controller.AdminUserController', 'base--用户管理', '登录', '[\"wangsong\",\"527w10n8c\"]', '{\"code\":200,\"data\":true,\"msg\":\"成功\"}', 1, 44, 44);
INSERT INTO `t_xj_admin_log` VALUES ('1377868475904593921', NULL, NULL, '2021-04-02 14:20:18', '2021-04-02 14:20:18', 0, 0, '兮家小二', '1', 0, 'http://xijia.plus/page/modules_sys_xj_help_helpUpd?id=1377844447580094465', 'http://xijia.plus/api/admin/xj/adminHelp/upd', '/api/admin/xj/adminHelp/upd', '125.70.195.54', '127.0.0.1', 'PUT', 'xijia.plus', '51502', 'com.ws.ldy.modules.sys.xj.controller', 'com.ws.ldy.modules.sys.xj.controller.XjAdminHelpController', 'base-plus--帮助中心', 'ID编辑', '[{\"category\":2,\"content\":\"## 接口验签使用文档\\n接口验签主要目的是为了防止数据在传输过程中被篡改\\n\\n如何防止 :\\n- 1、传递的参数+当前时间戳进行参数加密获得sign, 前后端使用相同的加签方式来验证,相同通过, 可能请求被拦截篡改\\n- 2、使用时间戳 timestamp 验证接口请求时长,如何超过N秒服务器才接收到请求,将被判定位无效请求, 可能请求被拦截篡改\\n\\n### 一、加签规则\\n\\n加签验签所有 appKey + secretKey 作为秘钥参数, 前后端需自行约定\\n\\n#### 1.1、query加签规则\\n \\n - 1、获取请求参数, 直接获取请求url ? 后面的参数, 并定义对象(key，value)存放参数\\n - 2、对象参数中追加 timestamp\\n - 3、对象参数根据 key 按ASCII码进行排序, 包括 timestamp 参数\\n - 4、对排序后的参数进行md5加密, 示例：` md5(appKey + 排序后参数对象 + secretKey)`\\n - 5、sign + timestamp 放入 headers 进行请求\\n \\n=其他说明：空value的参数对象(key+value) 不加入验签范围, 在第一步时处理,为空的不放入接收参数的对象中\\n\\n\\n#### 1.2、body加签规则\\n- 1、获取请求参数, 对 body参数内的 key 按ASCII码进行排序 (包括所有Tree结构下级数据), \\n然后把排序后的对象转为json字符串,  定义对象参数, 然后添加参数 body=bodyJson \\n示例代码: \\n\\n```bash\\n  data = Sign.bodyDataSort(data);        // 对body参数进行排序\\n  let bodyJson = JSON.stringify(data);   // 转为json\\n  let theRequest = {};\\n  theRequest[\\\"body\\\"] = bodyJson;          // 加签参数(key=body, value=bodyJson)\\n```\\n\\n后续步骤与 query 加签规则相同\\n- 2、追加query格式的参数 timestamp\\n- 3、参数对象根据 key 排序, 只有 body + timestamp 两个参数\\n- 4、md5(appKey + 排序后的参数 + secretKey)\\n- 5、sign + timestamp 放入 headers 进行请求\\n\\n\\n\\n### 二、注意事项 及 配置\\n\\n#### 2.1、md5 加密工具(前端+后端)\\n\\nmd5 加密工具前后端一点要一致, 当前为加密为 32位小写字母, 同时加密会处理中文字符 (部分Md5工具出现中文会导致前后端加签参数不一致),  请使用该文档 附带的MD5 工具类\\n\\n#### 2.2、获取body参数（后端）\\n后端 body 参数只可以获取一次, 所以会导致后续操作读取body参数失败, 目前使用定义RequestWrapper来 重写 getInputStream() , 把body参数缓存到RequestWrapper来处理\\n\\n#### 2.3、文件处理（后端）\\n目前无法对文件验签, 目前直接判断请求类型为 multipart/form-data 的直接进行放行操作\\n\\n#### 2.4、静态页面（后端）\\n如swagger的接口文档的静态文件css,html,js 等文件, 需自定义配置放行操作\\n如需配置更多放行, 在 SysSingFilter 的 excludeUriList 添加配置放行地址即可\\n\\n\\n#### 2.5、传参（前端）\\n1、不能同时传递 query参数和 body参数 \\n2、如果query参数和 body参数 都没有传递直接判定为验签成功\\n3、默认会先进行query验签, 如果没有query参数, 在判断是否存在body参数,存在进行验签\\n\\nps: 特殊字符处理，如 #, 不能使用query直接传递, 否则后端无法获取, 所以在传递时可能会被进行 encodeURIComponent编码, 防止验签失败, 所以在前端获取 query 参数是统一使用decodeURIComponent() 方法进行解码操作, 获取原始参数\\n\\n\\n#### 2.6、验签配置 (前端+后端)\\n后端加签加签秘钥 及 访问有效期在 SignUtil 中进行配置\\n\\n1、APP_KEY + SECRET_KEY, 前后端需约定,必须相同才能验签成功, 前端注意尽量不要暴露此参数给用户, 包括 F12 查看\\n2、TIME_OUT 超时时间, 如何验签接口发起后 N 秒(TIME_OUT 毫秒)  内才开始验签, 直接判定为验签失败\\n\\n#### 2.7、加签前的排序 (前端+后端)\\n1、query 参数只有一层, 直接根据key的ASCII码排序即可\\n\\n2、body 参数可能存在N级, 每一级的数据都需要 当前层级 key 的 ASCII码 进行排序, 除对象内不是数组的测试：如： [1,3,2]\\n\\n示例1, 需解析排序 (数组->对象 )\\n```bash\\n [{\\\"a\\\":1},{\\\"b\\\":1}]\\n ```\\n示例2, 需解析排序 (对象)\\n ```bash\\n {\\\"a\\\":1,\\\"b\\\":1}\\n ```\\n 示例3 ,需解析排序 (对象 -> 数组 || 对象 -> 对象 || 数组 -> 对象 )\\n  ```bash\\n\\n ## 就是 第一层和下层 不管是数组还是对象, 都需要可以进行解析排序\\n {\\\"a\\\":1,\\n   \\\"b\\\": {\\n       \\\"a\\\":1,\\n\\t\\\"b\\\":1\\n   },\\n   \\\"c\\\":[\\n       {\\\"a\\\":1},\\n       {\\\"b\\\":1}\\n   ]\\n }\\n```\\n示例4 (数组参数), 不解析不排序\\n```bash\\n [1,2,3,5,4]\\n ||\\n {\\n  \\\"a\\\":1,\\n  \\\"b\\\": [1,2,3,5,4] \\n }\\n# ps: b 相当于只是相当于其中的一个参数, 无需排序\\n```\\n 前端需自行排序，后端可使用 `JSONObject.toJSONString(obj, SerializerFeature.MapSortField) ` 进行排序\\n\\n\\n\\n### 3、后端加签代码\\n\\n后端加签代码:\\nhttps://gitee.com/wslxm/spring-boot-plus2/tree/v-0.1.0/xj-base/xj-base-admin/src/main/java/com/ws/ldy/config/sing\\n\\n后端加签中部分返回 R 及 RType 参数相关参数查看：\\nhttps://gitee.com/wslxm/spring-boot-plus2/tree/v-0.1.0/xj-base/xj-base-result/src/main/java/com/ws/ldy/common/result\\n\\n### 4、前端加签代码(js版)\\n\\n#### 4.1、前端md5 工具类 SignMD5.js\\n\\nSignMD5.js 地址：\\nhttps://gitee.com/wslxm/spring-boot-plus2/blob/v-0.1.0/xj-base/xj-base-admin-ui/src/main/resources/static/base/js/SignMD5.js\\n\\n\\n#### 4.2、加签代码\\n\\n\\n```javascript\\n\\n/**\\n * 加签\\n * <P>\\n *   验签\\n *  <P>\\n * account=1720696548&password=123456&timestamp=1578811547552\\n * sign,timestamp\\n * String mysign = DigestUtils.md5Hex(getContentBytes(preSignStr + APP_KEY, INPUT_CHARSET));\\n * @author wangsong\\n * @mail  1720696548@qq.com\\n * @date  2021/3/29 0029 22:10\\n * @version 1.0.0\\n */\\nSign = {\\n    /**\\n     * 加签秘钥\\n     */\\n    param: {\\n        appKey: \\\"xijia\\\",\\n        secretKey: \\\"xijia@qwer\\\",\\n    },\\n    /**\\n     * 1、query 参数加签\\n     * <p>\\n     *     1、获取请求参数, 直接获取url ? 后面的参数\\n     *\\n     *     // --- addSing 方法\\n     *     2、追加 timestamp\\n     *     3、参数对象根据 key 排序(包括 timestamp)\\n     *     4、md5(appKey + 排序后的参数 + secretKey)\\n     *\\n     *     // --- 请求\\n     *     5、sign + timestamp 放入 headers 进行请求\\n     *     其他：空val的对象不加入验签范围\\n     * </p>\\n     * @param url 请求的 url\\n     * @param timestamp 时间戳\\n     * @returns {string|null}\\n     */\\n    query: function (url, timestamp) {\\n        if (url.indexOf(\\\"?\\\") === -1) {\\n            return null;\\n        }\\n        // 通过split()分割为数组\\n        let arr = url.split(\'?\')[1].split(\'&\');\\n        // 加签参数, query的所有参数\\n        let theRequest = {};\\n        for (let i = 0; i < arr.length; i++) {\\n            let kye = arr[i].split(\\\"=\\\")[0];\\n            let value = arr[i].split(\\\"=\\\")[1];\\n            if (value != null && value !== \\\"\\\") {\\n                // value不为null给对象赋值，decodeURIComponent目的是为了参数出现 # 等的字符,在请求前进行了encodeURIComponent编码\\n                theRequest[kye] = decodeURIComponent(value);\\n            }\\n        }\\n        return Sign.addSing(theRequest, timestamp);\\n\\n    },\\n\\n    /**\\n     * 2、body 参数加签\\n     * <p>\\n     *     1、获取请求参数, 对 body 的参数内的参数进行key排序 (包括所有下级数据排序), 然后转化为json字符串\\n     *       - 并以 query格式拼接参数，示例: body= JSON(key排序后data)\\n     *\\n     *     // --- addSing 方法\\n     *     2、追加query格式的参数 timestamp\\n     *     3、参数对象根据 key 排序, 只有 body + timestamp 两个参数\\n     *     4、md5(appKey + 排序后的参数 + secretKey)\\n     *\\n     *     // ---- 请求\\n     *     5、sign + timestamp 放入 headers 进行请求\\n     *     其他：空val的对象不加入验签范围\\n     * </p>\\n     * @param data body数据\\n     * @param timestamp 当前数据戳\\n     * @returns {null}\\n     */\\n    body: function (data, timestamp) {\\n        if (data == null) {\\n            return null;\\n        }\\n        // body参数排序\\n        data = Sign.bodyDataSort(data);\\n        // 转为json\\n        let bodyJson = JSON.stringify(data);\\n        // 加签参数\\n        let theRequest = {};\\n        theRequest[\\\"body\\\"] = bodyJson;\\n        return Sign.addSing(theRequest, timestamp);\\n    },\\n\\n    /**\\n     * 3、加签实现\\n     *    3、参数对象根据 key 排序, 只有 body + timestamp 两个参数\\n     *    4、md5(appKey + 排序后的参数 + secretKey)\\n     * @returns {*}\\n     */\\n    addSing: function (theRequest, timestamp) {\\n        theRequest[\\\"timestamp\\\"] = timestamp;\\n        // 排序并重新封装获得排序后的参数\\n        let dataParams = \\\"\\\";\\n        let result = Object.keys(theRequest).sort();\\n        for (let key of result) {\\n            dataParams += \\\"&\\\" + key + \\\"=\\\" + theRequest[key];\\n        }\\n        dataParams = dataParams.substring(1);\\n        // 加签\\n        return md5(Sign.param.appKey + dataParams + Sign.param.secretKey);\\n    },\\n\\n    /**\\n     * 4、body 参数排序\\n     * @returns {*}\\n     */\\n    bodyDataSort: function (data) {\\n        // 数组长度小于2 或 没有指定排序字段 或 不是json格式数据\\n        // 判断是数组还是对象\\n        if (data instanceof Array) {\\n            //  数组\\n            if (data[0] instanceof Object || data[0] instanceof Array) {\\n                let arrays = data;\\n                let newArrays = [];\\n                for (let i = 0; i < arrays.length; i++) {\\n                    let dataTwo = arrays[i];\\n                    // 根据 key 类型排序\\n                    let keysTwo = Object.keys(dataTwo).sort();\\n                    let newDataTwo = {};\\n                    for (let keyTwo of keysTwo) { //遍历json对象的每个key/value对,p为key\\n                        // 递归给下级排序\\n                        if (dataTwo[keyTwo] instanceof Object) {\\n                            newDataTwo[keyTwo] = Sign.bodyDataSort(dataTwo[keyTwo]);\\n                        } else {\\n                            newDataTwo[keyTwo] = dataTwo[keyTwo];\\n                        }\\n                    }\\n                    newArrays.push(newDataTwo);\\n                }\\n                return newArrays;\\n            } else {\\n                // 不处理: [0,1,2,3] 数组数据\\n                return data;\\n            }\\n        } else if (data instanceof Object) {\\n            // 对象\\n            // 根据 key 类型排序\\n            let keys = Object.keys(data).sort();\\n            let newData = {};\\n            for (let key of keys) { //遍历json对象的每个key/value对,p为key\\n                // 递归给下级排序\\n                if (data[key] instanceof Object) {\\n                    newData[key] = Sign.bodyDataSort(data[key]);\\n                } else {\\n                    newData[key] = data[key];\\n                }\\n            }\\n            return newData;\\n        } else {\\n            return data;\\n        }\\n    }\\n};\\n\\n```\\n#### 4.3、加签代码请求处调用\\n```javascript\\n    // 加签\\n    let timestamp = new Date().getTime();\\n    let sign = Sign.query(url, timestamp);\\n    sign = sign != null ? sign : Sign.body(data, timestamp);\\n\\n    // 放入请求头\\n      headers: {\\n          \\\"TOKEN\\\": getGlobalHeaders(),\\n          \\\"timestamp\\\": timestamp,\\n          \\\"sign\\\": sign,\\n       },\\n```\\n\\n\\n\",\"helpVersion\":0,\"id\":\"1377844447580094465\",\"sort\":0,\"title\":\"接口验签\"}]', '{\"code\":200,\"data\":\"1377844447580094465\",\"msg\":\"编辑成功\"}', 1, 31, 30);
