package com.ws.ldy.others.base.model.convert;import com.ws.ldy.common.utils.BeanDtoVoUtil;import java.io.Serializable;/** *  java 实体类entity/DTO/VO 相互转换 * * @author ws * @mail 1720696548@qq.com * @date 2020/3/31 0031 15:26 * @return */public class Convert implements Serializable {    /**     * 获取自动转换后的JavaBean对象     *     * @param clazz     * @param <T>     * @return     */    public <T> T convert(Class<T> clazz) {        return BeanDtoVoUtil.convert(this, clazz);    }}